import React from "react";
import { Route, Link} from 'react-router-dom'
import Article from './Article/Article'
import './Headlines.css' 

const Headlines = props => {
  let newsList = props.nytData.results.map(article => {

    const handleClick = async (e) =>{
      props.setNytArticle(article)
    }

    return <li onClick={handleClick}>
        <Link to={"/article/"+article.uri}>{article.title}</Link>
        </li>;
  });

  return (
    <div>
        {props.nytArticle.title ? <Route path='/article/:title' render={props => <Article  handleListToggle={props.handleListToggle}/> }/>: null}
      <ul >{newsList}</ul>
    </div>
  );
};

export default Headlines;
