import React, { useState, useEffect } from "react";
import Headlines from "./components/Headlines/Headlines";
import Article from "./components/Article/Article";
import List from "./components/List/List";
import "./App.css";
import {Route, Link} from 'react-router-dom'

const App = () => {
  const [nytData, setNytData] = useState([]);
  const [nytArticle, setNytArticle] = useState({loadingGif});
  const [readingList, setReadingList] = useState([]);
  const [filter, setFilter] = useState('all');


  useEffect(() => {
    //console.log("inside useEffect - componentDidMount");
    const NYTdata =
      "https://api.nytimes.com/svc/topstories/v2/home.json?api-key=i2JyHtAdfPqB0jQkirLcwBVb7xyCv0US";
      const loadingGif = 'https://media.giphy.com/media/GCjueAStKH9yU/giphy.gif'
    const makeApiCall = async () => {
      const res = await fetch(NYTdata);
      const json = await res.json();
      setNytData(json.results);
    const setLoading = async () =>{
      const res = await fetch(loadingGif);
      const gif = await res.json();
      setNytArticle(
        multimedia[2].url=gif,
        title='dsffgs',
        abstract='dsafa',
        url='adfagag'
      )
    }
    };
    makeApiCall();
    setLoading();
  }, []);




  const handleListToggle = article => {
    const listArr = readingList.slice();
    const articleIndex = listArr.indexOf(article);
    if (articleIndex > -1) {
      listArr.splice(articleIndex, 1);
    } else {
      listArr.push(article);
    }
    setReadingList(listArr);
    console.log('listArr',listArr)
  };
  console.log('readingList',readingList)

  console.log("loaded API", nytData);

  const handleFilterClick = filter => {
    console.log(`setting filter to ${filter}`);
    setFilter(filter);

  };
  const displayData = filter === "list" ? readingList : nytData;
  console.log('filter',filter)
  return (
    <div className="App">
      <div className="header">
        <h1 onClick={()=> window.location.reload(false)}>Headlines</h1>
        {nytArticle.title ? <Route path='/article/:title' render={props => <Article nytArticle={nytArticle} handleListToggle={handleListToggle} readingList={readingList}/> }/>: null}
      </div>
      <nav>
        <p onClick={() => handleFilterClick("all")}>Top Stories</p>
        <p onClick={() => handleFilterClick("list")}>My List</p>
      </nav>
      <div className="scrollbox">
        {nytData ? <Route path='/' render={props => <Headlines nytData={displayData} setNytArticle={setNytArticle}/> }/>: null}
      </div>
    </div>
  );
};
export default App;
